import React, { useState } from "react";
import {
  Card,NavLink,UncontrolledTooltip , CardBody, 
  CardTitle,Form,UncontrolledAlert,FormGroup,Label,Input,Col,Button
} from "reactstrap";
import userData from './../../data/userData.json';
import { useHistory } from "react-router-dom";
import icon from "./../../images/signup.jpeg";
import {
    BrowserRouter as Router,
    Switch,
    Route,
    Link
  } from "react-router-dom";
export const SignUp = () => {
  const [values,setValues] = useState({
    username:""
  })
  const history = useHistory();
  const [username,setUserName]= useState('');
  const [email, setEmail] = useState('');
  const [password, setPassword] = useState('');
  const [repassword,setRepassword] = useState('') ;
  const [error, setError] = useState('');

  const onClick = (event) => {
    event.preventDefault();
    // if(email && password){
    //   const user = userData.filter(user => user.email === email && user.password === password);
    //   if(user.length > 0){
    //     localStorage.setItem('user', user);
    //     history.push('/user-page')
    //   }else{
    //     setError('Sorry, we could not verify your credentials');
    //   }
    // }else{
    //   setError('Please check your credentials')
    // }
  }

  return (
    <div>
    <Card fluid className="col-xs-8 col-sm-8 col-lg-4" style={{margin:"2rem"}}>
      <CardBody>
        <CardTitle tag="h1">Sign Up</CardTitle>
      </CardBody>
      <img className="ms-4" width="15%" src={icon} alt="welcome" />
      <CardBody>
      <Form>
      {error && <UncontrolledAlert color="danger" fade={true}>
        I am an alert and I can be dismissed without animating!
      </UncontrolledAlert>}
      <FormGroup row>
        <Label for="examplename" sm={2}>
          Name:
        </Label>
        <Col className="col-sm-7 col-lg-7 col-md-7 col-xs-7">
          <Input
            value={values.username}
            onChange={(e) => setUserName(e.target.value)}
            type="name"
            name="username"
            id="examplename"
            placeholder="Enter your username"
          />
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label for="exampleEmail" sm={2}>
          Email:
        </Label>
        <Col className="col-sm-7 col-lg-7 col-md-7 col-xs-7">
          <Input
            value={email}
            onChange={(e) => setEmail(e.target.value)}
            type="email"
            name="email"
            id="exampleemail"
            placeholder="Enter your email address"
          />
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label for="examplePassword" sm={2}>
          Password:
        </Label>
        <Col className="col-sm-7 col-lg-7 col-md-7 col-xs-7">
          <Input
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            type="password"
            name="password"
            id="examplePassword"
            placeholder="Enter your password"
          />
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label for="examplePassword2" sm={10}>
          Re-enter Your Password:
        </Label>
        <Col style={{paddingLeft:"19%"}} className="col-sm-9 col-lg-9 col-md-9">
          <Input
            value={repassword}
            onChange={(e) => setRepassword(e.target.value)}
            type="repassword"
            name="repassword"
            id="examplerePassword"
            placeholder="Enter your password again"
          />
        </Col>
      </FormGroup>
      <FormGroup row>
      <Label for="exampleDate" sm={2}>Birthday:</Label>
      <Col className="col-sm-7 col-lg-7 col-md-7 col-xs-7">
        <Input
          type="date"
          name="date"
          id="exampleDate"
          placeholder="date placeholder"
        /></Col>
      </FormGroup>
      <FormGroup row>
        <Label for="exampleSelect" sm={2}>Gender Identity:</Label>
        <Col className="col-sm-7 col-lg-7 col-md-7 col-xs-7">
        <Input type="select" name="select" id="exampleSelect">
          <option>Female</option>
          <option>Male</option>
        </Input>
        </Col>
      </FormGroup>
      <FormGroup row>
        <Label for="exampleSelectMulti" sm={2}>Horoscope:</Label>
        <Col className="col-sm-7 col-lg-7 col-md-7 col-xs-7">
        <Input type="select" name="selectmulti" id="exampleSelectMulti" multiple>
          <option>Aries</option>
          <option>Taurus</option>
          <option>Gemini</option>
          <option>Cancer</option>
          <option>Leo</option>
          <option>Libra</option>
          <option>Virgo</option>
          <option>Scorpio</option>
          <option>Sagittarius</option>
          <option>Capricorn</option>
          <option>Aquarius</option>
          <option>Pisces</option>
        </Input>
        </Col>
      </FormGroup>
      <FormGroup check row>
      <span><NavLink id="UncontrolledTooltipExample" strict to="/events/">
  Horoscope
</NavLink></span>
<UncontrolledTooltip placement="right" target="UncontrolledTooltipExample">
        It is made for who does not know what his/her horoscope is.
      </UncontrolledTooltip>
      </FormGroup>
      <FormGroup check row>
        <Label check>
          <Input type="checkbox" />I accept the agreement.
        </Label>
      </FormGroup>
      <FormGroup check row>
        <Col sm={{ size: 10, offset: 2 }}>
          <Button onClick={onClick}>Submit</Button>
        </Col>
      </FormGroup>
    </Form>
      </CardBody>
    </Card>
  </div>



  );
};
