 const validation = (values) => {
    let errors= {} ;
    if(!values.username||!(3<values.username.length<11)){
        console.log(values.username.length)
        errors.username="Username length must be between 3 and 11!"
    }
    if(!values.email){
        errors.email="Email must be required!" 
    }else if(!/\S+@\S+\.\S+/.test(values.email)){
        errors.email="Email is invalid!"
    }
    if(!values.password||!(5<values.password.length<16)){
        errors.password="Password length must be between 5 and 16!"
    }
    if(values.repassword!==values.password){
        errors.repassword="Two passwords should be equal!"
    }
    return errors;
}
export default validation;
